mapper.select:
SELECT ROWNUM AS SEQ , IP , RECENT_USER , USE_YN , FORWHAT , TO_CHAR(ENT_DATE, 'YYYY/MM/DD') AS ENT_DATE , TO_CHAR(MOD_DATE, 'YYYY/MM/DD') AS MOD_DATE FROM STUDY.DEPT_IP_MASTER WHERE 1=1 AND USE_YN = 'Y'
AND IP = #{userIp}
ORDER BY IP ASC

mapper.update:
MERGE INTO STUDY.DEPT_IP_MASTER USING DUAL
   ON (
         IP = #{userIp}
   )
WHEN MATCHED THEN
UPDATE
   SET 
       RECENT_USER = #{userName, jdbcType=VARCHAR}
      ,FORWHAT     = #{reason, jdbcType=VARCHAR}
      ,PASSWORD    = #{passwd, jdbcType=VARCHAR}
      ,USE_YN      = 'Y'
      ,ENT_DATE    = SYSDATE
WHEN NOT MATCHED THEN
INSERT
     (
       IP
      ,RECENT_USER
      ,USE_YN
      ,FORWHAT
      ,PASSWORD
      ,ENT_DATE    	     
     )
VALUES
(
	#{userIp}
   ,#{userName, jdbcType=VARCHAR}
   ,'Y'
   ,#{reason, jdbcType=VARCHAR}
   ,#{passwd, jdbcType=VARCHAR}
   ,SYSDATE
)

MERGE INTO STUDY.DEPT_IP_MASTER USING DUAL
   ON (
         IP = #{userIp}
   )
WHEN MATCHED THEN
UPDATE
   SET 
       RECENT_USER = #{userName, jdbcType=VARCHAR}
      ,FORWHAT     = #{reason, jdbcType=VARCHAR}
      ,USE_YN      = 'Y'
      ,MOD_DATE    = SYSDATE
WHEN NOT MATCHED THEN
INSERT
     (
       IP
      ,RECENT_USER
      ,USE_YN
      ,FORWHAT
      ,MOD_DATE    	     
     )
VALUES
(
	#{userIp}
   ,#{userName, jdbcType=VARCHAR}
   ,'Y'
   ,#{reason, jdbcType=VARCHAR}
   ,SYSDATE
)

mapper.select:
SELECT CASE WHEN COUNT(1) > 0 THEN 'Y'
            ELSE 'N'
       END AS PASS_YN
  FROM STUDY.DEPT_IP_MASTER
 WHERE IP       = #{userIp}
   AND PASSWORD = #{passwd, jdbcType=VARCHAR}

mapper.insert:
	INSERT INTO STUDY.DEPT_IP_HIST
	       (
	       	 IP
	       	,SEQ
	       	,USER_NAME
	       	,ACT_CODE
	       	,ACT_NAME
	       	,MOD_ID
	       	,MOD_DATE
	       )
    VALUES
          (
            #{userIp}
           ,(SELECT NVL(MAX(SEQ),0)+1 FROM STUDY.DEPT_IP_HIST WHERE IP = #{userIp})
           ,#{userName, jdbcType=VARCHAR}
           ,#{acionCode}
           ,STUDY.FN_GET_COMN_NM('L0001', #{acionCode})
           ,#{userId}
           ,SYSDATE
          )
