mapper.select:
SELECT * FROM MGR
WHERE MGR_ID = #{mgr_id}
  AND MGR_NAME = #{mgr_name}

mapper.update:
UPDATE MGR
   SET CERT_NUM = #{certNum}
 WHERE MGR_ID = #{mgrId}

UPDATE MGR
   SET PWD_CERT_NUM = #{certNum}
      ,PWD_CERT_SEND_DATE = SYSDATE
 WHERE MGR_ID = #{mgrId}

mapper.select:
SELECT A.MGR_ID , A.MGR_NAME , A.MGR_SYS , A.MGR_GRADE , A.CERT_NUM , A.MGR_PWD , DATE_FORMAT(MGR_PWD_CHG_DATE, '%Y/%m/%d %H:%i:%s') AS MGR_PWD_CHG_DATE , A.MGR_STATE_CD , A.MGR_TASK , A.LOGIN_FAIL_CNT , B.ROLE_GROUP FROM MGR A LEFT OUTER JOIN CM_ROLE_GROUP B ON A.MGR_GRADE = B.AUTH_GROUP_ID WHERE A.MGR_ID = #{mgrId}
AND A.CERT_NUM = #{certNum}
AND A.MGR_PWD = XX1.ENC_VARCHAR2_INS(#{userPwd}, 10, 'PWD')
AND A.MGR_STATE_CD = #{mgrStateCd, jdbcType=VARCHAR}

mapper.insert:
INSERT INTO MGR (
	  MGR_ID
	, MGR_NAME
	, MGR_SYS
	, MGR_GRADE
	, CERT_NUM
	, MGR_PWD
	, MGR_PWD_CHG_DATE
	, MGR_STATE_CD
	, MGR_TASK
) VALUES (
	  #{mgrId, jdbcType=VARCHAR}
	, #{mgrName, jdbcType=VARCHAR}
	, #{mgrSys, jdbcType=VARCHAR}
	, #{mgrGrade, jdbcType=VARCHAR}
	, null
	, XX1.ENC_VARCHAR2_INS(#{mgrPwd, jdbcType=VARCHAR}, 10, 'PWD')
	, SYSDATE
	, #{mgrStateCd}
	, #{mgrTask}
)

mapper.select:
SELECT EMPNO , NAME FROM SSO_DEPT.TSSUSR# WHERE 1 = 1
AND EMPNO = #{mgrId}
AND RETGBN = #{retireGubun} AND CHG_DATE >= TO_CHAR(SYSDATE - 1, 'YYYYMMDD')

SELECT EMPNO , NAME FROM SSO_DEPT.TSSUSR# WHERE 1 = 1
AND EMPNO = #{mgrId}
AND RETGBN = #{retireGubun} AND CHG_DATE >= TO_CHAR(SYSDATE - 1, 'YYYYMMDD')

mapper.update:
UPDATE MGR
    SET LOGIN_FAIL_CNT = CASE WHEN #{successYn} = 'Y' THEN 0
    						  ELSE LOGIN_FAIL_CNT + 1
    				     END
	
		, MGR_STATE_CD = #{mgrStateCd}		   
	
	
		, SESSION_CRE_DTM = #{sessionCreDtm}		   
	
	
		, SESSION_ID = #{loginSsid}		   
	
 WHERE MGR_ID = #{mgrId, jdbcType=VARCHAR}

mapper.insert:
		INSERT INTO MGR_LOGIN_HIS (
