			, SYSDATE
		)

INSERT INTO MGR_PWD_HIST
       ( MGR_ID
        ,MGR_PWD
        ,CHANGE_DT
       ) 
 VALUES
       ( #{mgrId}
        ,XX1.ENC_VARCHAR2_INS(#{mgrPwd, jdbcType=VARCHAR}, 10, 'PWD', 'MGR_PWD_HIST', 'PWD_NO')
        ,SYSDATE
       )

mapper.delete:
DELETE
FROM MGR_PWD_HIST
WHERE MGR_ID = #{mgrId}
  AND CHANGE_DT IN ( SELECT MIN(CHANGE_DT)
    					  FROM MGR_PWD_HIST
    					  WHERE MGR_ID = #{mgrId}
    					)

mapper.update:
      /*+ updateUserStatInfo */
      MERGE INTO MGR USING DUAL
	       ON (
		           MGR_ID = #{mgrId}
		       )
		WHEN MATCHED THEN
			UPDATE
	       	   SET SESSION_CRE_DTM = #{sessionCreDtm}
		         , SESSION_ID = #{ssId}
		WHEN NOT MATCHED THEN
			NULL

mapper.select:
    /*+ selectCurrentUserStat */
	SELECT CASE WHEN COUNT(1) > 0 THEN 'N' ELSE 'Y' END AS DUP_LOGINYN
	 FROM  MGR
	 WHERE MGR_ID = #{mgrId}
	       AND SESSION_CRE_DTM = #{sessionCreDtm}
	       AND SESSION_ID = #{ssId}

    /*+ checkPwdOtpInfo */
	SELECT CASE WHEN COUNT(1) > 0 THEN 'Y' ELSE 'N' END AS OTP_YN
	 FROM  MGR
	 WHERE MGR_ID = #{mgrId}
	   AND PWD_CERT_NUM = #{certNum}
	   AND (TO_DATE(TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS'), 'YYYYMMDDHH24MISS') - PWD_CERT_SEND_DATE) * 24 * 60 * 60  <=  190
