mapper.select:

 SELECT SQL_TEXT
   FROM V$SQLTEXT
  WHERE ADDRESS = #{SQL_ADDRESS}
    AND HASH_VALUE = #{SQL_HASH_VALUE}
ORDER BY PIECE

SELECT * FROM (SELECT
/*+ INDEX_DESC(A PK_TCKLOQ) */
/*+ INDEX_DESC(A TCKLOQ_IX01) */
ROWNUM AS RNUM , LAST_LOAD_YMD , DB_NAME , SQL_ID , LAST_LOAD_TIME , ELAPSED_TIME , LAST_LOAD_YMD || LAST_LOAD_TIME AS EXEC_DT , EXECUTIONS , ROWS_PROCESSED FROM TCKLOQ A WHERE 1 = 1
AND DBMS_LOB.COMPARE(A.SQL_FULLTEXT, (SELECT SQL_FULLTEXT FROM TCKLOQ WHERE DB_NAME = #{db_name} AND SQL_ID = #{sql_id} AND ROWNUM <2) ) = 0
AND DB_NAME = #{db_name} AND LAST_LOAD_YMD LIKE TO_CHAR(SYSDATE, 'YYYYMM') || '%' AND ROWNUM <= #{start} + #{length} ) WHERE RNUM BETWEEN (#{start} + 1) AND #{start} + #{length}

SELECT * FROM (SELECT ROWNUM AS RNUM , A.* FROM (SELECT /*+ INDEX_DESC(A TCKLOQ_IX01) */ SUBSTR(LAST_LOAD_YMD, 1, 6) AS LAST_LOAD_YMD , DB_NAME , SQL_ID , ROUND(AVG(ELAPSED_TIME), 2) AS ELAPSED_TIME , SUM(EXECUTIONS) AS EXECUTIONS , ROUND(AVG(ROWS_PROCESSED), 2) AS ROWS_PROCESSED FROM TCKLOQ A WHERE 1 = 1
AND DBMS_LOB.COMPARE(A.SQL_FULLTEXT, (SELECT SQL_FULLTEXT FROM TCKLOQ WHERE DB_NAME = #{db_name} AND SQL_ID = #{sql_id} AND ROWNUM <2) ) = 0
AND DB_NAME = #{db_name} GROUP BY SUBSTR(LAST_LOAD_YMD, 1, 6), DB_NAME, SQL_ID ORDER BY 4 DESC ) A WHERE ROWNUM <= #{start} + #{length} ) WHERE RNUM BETWEEN #{start} + 1 AND #{start} + #{length}

SELECT A.LAST_LOAD_YMD , A.DB_NAME , A.SQL_ID , A.LAST_LOAD_TIME , REGEXP_REPLACE(A.FIRST_LOAD_TIME, '[-/:]', '') AS FIRST_LOAD_TIME , A.BUFFER_GETS , A.DISK_READS , A.ROWS_PROCESSED , A.ELAPSED_TIME , A.EXECUTIONS , A.SQL_FULLTEXT FROM TCKLOQ A WHERE A.LAST_LOAD_YMD LIKE #{LAST_LOAD_YMD} || '%' AND A.DB_NAME = #{DB_NAME} AND A.SQL_ID = #{SQL_ID}
AND A.LAST_LOAD_TIME = #{LAST_LOAD_TIME}
AND ROWNUM < 2

    
		SELECT A.BIND_ID
		        , A.POSITION
		        , A.DATATYPE
		        , XX1.DEC_VARCHAR2_SEL(A.VALUE_STRING, 10, 'PHONE') AS VALUE_STRING
		  FROM TCKLOQ_PARAM A
		 WHERE A.LAST_LOAD_YMD = #{LAST_LOAD_YMD}
		    AND A.DB_NAME = #{DB_NAME}
		    AND A.SQL_ID = #{SQL_ID}
		    AND A.LAST_LOAD_TIME = #{LAST_LOAD_TIME}
