mapper.select:
SELECT AUTH.ROLE_CODE , SYS.GROUP_CODE , SYS.SYSTEM_CODE , DCO.EVENT_YMD , SYS.SYSTEM_NAME AS DB_NAME , DCO.DB_NAME AS DB_SID , DCO.OWNER , DCO.OBJECT_NAME , TBL.COMMENTS , DCO.OBJECT_TYPE , DCO.CREATED_YMD || DCO.CREATED_TIME AS CREATED_DATE , DCO.LAST_DDL_YMD || DCO.LAST_DDL_TIME AS LAST_DDL_DATE , DCO.STATUS , DCO.CONFIRM_GUBN , DCO.CONFIRM_YN , DCO.CONFIRM_DATE , DCO.CONFIRM_EMPNO FROM TCKDCO DCO, TCKSYS SYS, TCKTBL TBL, ( SELECT C.GROUP_CODE, C.SYSTEM_CODE, C.DB_SID, A.EMPNO, A.ROLE_CODE FROM TCKRMU A, TCKRMS B, TCKSYS C WHERE A.ROLE_CODE = B.ROLE_CODE AND B.GROUP_CODE = C.GROUP_CODE AND B.SYSTEM_CODE = C.SYSTEM_CODE AND A.EMPNO = #{empNo} ) AUTH WHERE 1=1 AND DCO.DB_NAME = SYS.DB_SID AND SYS.GROUP_CODE = AUTH.GROUP_CODE AND SYS.SYSTEM_CODE = AUTH.SYSTEM_CODE AND DCO.DB_NAME = TBL.DB_SID AND DCO.OWNER = TBL.OWNER AND DCO.OBJECT_NAME = TBL.OBJECT_NAME AND (DCO.CONFIRM_YN = 'N' OR DCO.CONFIRM_YN IS NULL)
AND DCO.DB_NAME = #{dbName}
AND DCO.OWNER = #{owner}
AND DCO.OBJECT_TYPE = #{objectType}
AND DCO.OBJECT_NAME LIKE '%'|| #{objectName} || '%'

mapper.update:
UPDATE TCKDCO
   SET CONFIRM_DATE = SYSDATE
     , CONFIRM_EMPNO = #{empNo}
     , CONFIRM_YN = 'Y'
 WHERE 1=1
   AND EVENT_YMD = #{eventYmd}
   AND DB_NAME = #{dbName}
   AND OWNER = #{owner}
   AND OBJECT_NAME = #{objectName}
   AND OBJECT_TYPE = #{objectType}
