mapper.select:
SELECT DB_NAME, OWNER, TABLE_NAME, SQL, INFO, COUNT(*) OVER() AS TOTCNT, DSTRCTN_NMBR , DSTRCTN_CYCL , DSTRCTN_NMBR || ' ' || (SELECT ETC_NAME FROM TCKETC B WHERE ETC_GUBN = '008' AND B.ETC_CODE = A.DSTRCTN_CYCL) AS DSTRCTN_STNDRD FROM PRSNL_INFO_CHCK A WHERE 1=1
AND DB_NAME = #{dbName}
AND OWNER = #{owner}
AND TABLE_NAME = #{tableName}
ORDER BY DB_NAME, OWNER, TABLE_NAME

SELECT DISTINCT OBJECT_NAME AS TABLE_NAME FROM TCKTBL WHERE 1=1 AND OBJECT_TYPE = 'TABLE'
AND DB_SID = (SELECT DB_SID FROM TCKSYS WHERE SYSTEM_NAME = #{dbName})
AND OWNER = #{owner}

mapper.update:
MERGE INTO PRSNL_INFO_CHCK PIC
USING DUAL
ON (
	PIC.DB_NAME = #{dbName}
	AND PIC.OWNER = #{owner}
	AND PIC.TABLE_NAME = #{tableName}
)
WHEN MATCHED THEN
	UPDATE SET SQL = #{sqlData},
	           DSTRCTN_CYCL = #{stndrdCycle},
	           DSTRCTN_NMBR = #{stndrdNmbr},
			   INFO = #{info}
WHEN NOT MATCHED THEN
	INSERT VALUES (
		#{dbName},
		#{owner},
		#{tableName},
		#{sqlData},
		#{info},
		#{stndrdCycle},
		#{stndrdNmbr}
	)

mapper.delete:
DELETE FROM  
	PRSNL_INFO_CHCK PIC
WHERE 1=1
AND PIC.DB_NAME = #{dbName}
AND PIC.OWNER = #{owner}
AND PIC.TABLE_NAME = #{tableName}
AND PIC.SQL = #{sqlData}
