mapper.select:
SELECT T.*
FROM ( SELECT AA.* , (@ROWNUM := @ROWNUM + 1) AS RNUM , (@ROWNUM := @ROWNUM + 1) AS TOTCNT FROM ( SELECT TA.* FROM (SELECT @ROWNUM := 0) AS R , ( SELECT TBL.*, CASE WHEN ( SELECT COUNT(1) FROM TCKCOL_ADD COL WHERE 1=1 AND TBL.DB_SID = COL.DB_SID AND TBL.OWNER = COL.OWNER AND TBL.OBJECT_NAME = COL.TABLE_NAME AND COL.PERSONALINFO_YN IS NULL ) > 0 THEN '' WHEN ( SELECT COUNT(1) FROM TCKCOL_ADD COL WHERE 1=1 AND TBL.DB_SID = COL.DB_SID AND TBL.OWNER = COL.OWNER AND TBL.OBJECT_NAME = COL.TABLE_NAME AND COL.PERSONALINFO_YN = 'Y' ) > 0 THEN 'Y' ELSE 'N' END AS PERSONALINFO_FLAG FROM TCKTBL TBL ) TA WHERE 1=1
AND TA.DB_SID = #{dbName}
AND TA.OWNER = #{owner}
AND TA.OBJECT_TYPE = #{type}
AND TA.PERSONALINFO_FLAG = ''
AND TA.PERSONALINFO_FLAG = #{personalInfoFlag}
AND TA.STATUS = #{status}
AND TA.OBJECT_NAME LIKE '%' || #{objectName} || '%'
ORDER BY 1, 2, 3 ) AA ) T WHERE 1=1 AND RNUM BETWEEN #{start} AND #{end}

mapper.update:
MERGE INTO TCKCOL_ADD TA
USING DUAL
ON (
	TA.DB_SID = #{DB_SID}
	AND TA.OWNER = #{OWNER}
	AND TA.TABLE_NAME = #{TABLE_NAME}
	AND TA.COLUMN_NAME = #{COLUMN_NAME}
)
WHEN MATCHED THEN
	UPDATE SET PERSONALINFO_YN = #{PERSONALINFO_YN}
WHEN NOT MATCHED THEN
	INSERT VALUES (
		#{DB_SID}
		, #{OWNER}
		, #{TABLE_NAME}
		, #{COLUMN_NAME}
		, #{PERSONALINFO_YN}
	)

mapper.select:
SELECT * FROM TCKPER A WHERE NOT EXISTS( SELECT 1 FROM TCKPER_EX B WHERE A.DB_NAME = B.DB_NAME AND A.OWNER = B.OWNER AND A.TABLE_NAME = B.TABLE_NAME AND A.COLUMN_NAME = B.COLUMN_NAME AND A.CHECK_YMD = DATE_FORMAT(B.ENT_DATE, '%Y%m%d') )
AND A.CHECK_YMD BETWEEN #{startCheckDate} AND #{endCheckDate}
AND DATE_FORMAT(CONVERT(CHAR(8), A.CHECK_YMD, 112), '%Y%m') BETWEEN #{startCheckDate} AND #{endCheckDate}
AND A.DB_NAME = #{dbName}
AND A.CHECK_GUBN = #{infoType} ORDER BY A.CHECK_YMD DESC
